add_action("plugins_loaded", "DojoAccessibleCalendar_init");

function DojoAccessibleCalendar_init() {
    register_sidebar_widget(__('Dojo Accessible Calendar'), 'widget_DojoAccessibleCalendar');
    register_widget_control(   'Dojo Accessible Calendar', 'DojoAccessibleCalendar_control', 200, 200 );
    if (!is_admin() && is_active_widget('widget_DojoAccessibleCalendar')) {
        $options = get_option("widget_DojoAccessibleCalendar");
        if (!is_array($options)) {
            $options = array(
                'title' => 'Dojo Accessible Calendar',
                'language' => 'en',
                'theme' => 'tundra'
            );
        }
        $widgetTheme = $options['theme'];
        $widgetLang = $options['language'];
        // add your own script
        wp_register_style('digitTheme', ( get_bloginfo('wpurl') . '/wp-content/plugins/DojoAccessibleCalendar/dijit/themes/' . $widgetTheme . '/' . $widgetTheme . '.css'));
        wp_enqueue_style('digitTheme');
    }
}

function widget_DojoAccessibleCalendar($args) {
    extract($args);

    $options = get_option("widget_DojoAccessibleCalendar");
    if (!is_array( $options )) {
        $options = array(
                'title' => 'Dojo Accessible Calendar',
                'language' => 'en',
                'theme' => 'tundra'
        );
    }

    echo $before_widget;
    echo $before_title;
    echo $options['title'];
    echo $after_title;

    //Our Widget Content
    DojoAccessibleCalendarContent();
//    getDaysWithPosts();
    echo $after_widget;
}

function DojoAccessibleCalendarContent() {
    $options = get_option("widget_DojoAccessibleCalendar");
    if (!is_array( $options )) {
        $options = array(
                'title' => 'Dojo Accessible Calendar',
                'language' => 'en',
                'theme' => 'tundra'
        );
    }
    $widgetTheme = $options['theme'];
    echo '<p></p>
        <div align="center">
        <div class="'.$widgetTheme.'" dojoType="accessijit.DojoAccessibleCalendar"
            onChange="dojo.byId(\'formatted\').innerHTML=
            dojo.date.locale.format(arguments[0], {formatLength: \'full\', selector:\'date\'})"></div>
          <p id="formatted" tabindex = "0"></p></div>';
}

